// For CSS
declare module '*.m.css' {
  const classes: { [key: string]: string }
  export default classes
}

// For LESS
declare module '*.m.less' {
  const classes: { [key: string]: string }
  export default classes
}

// For SCSS
declare module '*.m.scss' {
  const classes: { [key: string]: string }
  export default classes
}
